package objects;

import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.net.URL;
import java.util.ArrayList;

import com.google.gson.JsonArray;
import com.google.gson.JsonObject;
import com.google.gson.JsonParser;

public class ImagesRequest {
	
	public ArrayList<String> imageResultURLs = new ArrayList<String>();

	String urlOfPrintable;
	
	String urlOfSearch;
	String searchTerm;
	Integer numResults;
	
	public ImagesRequest(String query) {
		searchTerm = query;
		
		if(query.contentEquals("hamburger")) {
			imageResultURLs.add("https://upload.wikimedia.org/wikipedia/commons/9/9a/Big_Mac_hamburger.jpg");
			imageResultURLs.add("https://www.recipetineats.com/wp-content/uploads/2016/02/Beef-Hamburgers_7-2.jpg");
			imageResultURLs.add("https://www.thespruceeats.com/thmb/ivY7T2DRygJN2nHwAFYqVxfbvs4=/3000x2000/filters:no_upscale():max_bytes(150000):strip_icc()/Hamburger-Hot-Dog-58add5f03df78c345bdef6ff.jpg");
			imageResultURLs.add("data:image/jpeg;base64,");
			imageResultURLs.add("https://www.justataste.com/wp-content/uploads/2013/05/easy-homemade-parmesan-hamburger-buns-recipe.jpg");
			imageResultURLs.add("https://www.saveur.com/sites/saveur.com/files/styles/1000_1x_/public/import/2014/2014-05/recipe_papas-favorite-wild-west-hamburger_i166_500x750.jpg?itok=QYuMyACp");
			imageResultURLs.add("https://assets.epicurious.com/photos/57c5c6d9cf9e9ad43de2d96e/master/pass/the-ultimate-hamburger.jpg");
			imageResultURLs.add("https://foremangrillrecipes.com/wp-content/uploads/2013/05/american-hamburger-foreman-grill.jpg");
			imageResultURLs.add("https://cdn.vox-cdn.com/thumbor/rRLHMuiSwa6khLkqQg9gH-sGS30=/0x0:617x521/1200x800/filters:focal(320x235:418x333)/cdn.vox-cdn.com/uploads/chorus_image/image/59948629/hamburger_hamelet_facebook.0.png");
			imageResultURLs.add("https://thumbor.forbes.com/thumbor/1280x868/https%3A%2F%2Fspecials-images.forbesimg.com%2Fdam%2Fimageserve%2F1164175639%2F960x0.jpg%3Ffit%3Dscale");	
		} else {
			this.request();
		}
		
	}
	
	private void request() {
		
		String key = "AIzaSyDVrkr5Sc_8D_qn4BsUepbG43zdALq84II";
		//String key = "AIzaSyCUBryJarHnnYvjEdJuI48R2ATEv3OvuP4"
		
		urlOfSearch = "https://www.googleapis.com/customsearch/v1?q=" + searchTerm + "&cx=010424164654780185781%3Adwdzcylugra&num=10&searchType=image&key=" + key;
		
		String storeJson = "";
		
		try {
			
			//GET request to custom search api
			
			URL url = new URL(urlOfSearch);
			BufferedReader br = new BufferedReader(new InputStreamReader(url.openStream()));
			
			String strTemp = "";
			
			//Store results of api call to storeJson string
			
			while ((strTemp = br.readLine()) != null) {
				storeJson += strTemp;
				storeJson += "\n";
			}
			
			
		} catch (Exception ex) {
			ex.printStackTrace();
		}
		
		//pull links from stored json response
		
		JsonObject jsonObject = new JsonParser().parse(storeJson).getAsJsonObject();

		JsonArray arr = jsonObject.getAsJsonArray("items");

		for (int i = 0; i < arr.size(); i++) {
		    String link = arr.get(i).getAsJsonObject().get("link").getAsString();
		    imageResultURLs.add(link);
		}

	}

}
